com.cwctravel.hudson.plugins.extended__c.groovyScript:
/* List all available test data sets */
def lookup_path = '/srv/jenkins/jobs/010-build/workspace/opg-core-back-end/fixtures/opg-core-ddc-essentials/DataSets';

def tags = [];
new File(lookup_path).eachDir() { dir -> tags.add(dir.getName()) }

def csv = tags.toString();
return csv.replaceAll(/[\[|\]]/, "");

/* List all available tags (descending order) */
def command = [
  'semvertag', '--cwd', '${JENKINS_HOME}/jobs/010-build/workspace/opg-core-back-end', 'list', '--csv'
];

def suffix = null;
new File(sprintf("%s/env", System.getenv('HOME'))).eachLine { line ->
  if (line.trim() && line =~ /DOCKER_SUFFIX/) {
    suffix = line.split('=').collect { it.trim() }.pop();
    return true;
  }
}

if (suffix?.trim() && suffix != "DOCKER_SUFFIX") {
  command.push('--stage');
  command.push(suffix);
}

def proc = command.execute();
proc.waitFor();

return proc.text.trim();

